pragma solidity ^0.4.21;

import "./ProxyContract.sol";

contract InitContract {
    function setValue(bytes32[] keys, bytes32[] values) 
    public 
    {
        require(keys.length == values.length);
        ProxyContract(0x2222222222222222222222222222222222222222).setValue(keys, values);
    }

    function close()
    public 
    {
        emit Status('destructing');
        selfdestruct(0x0);
    }

    event Status(string indexed status);
}
